import React, { Fragment, useState, useEffect, useRef } from 'react';
import Button from './Button';
import makeApiRequest from '../makeApiReqest';

function User({ user, isEdit = false }) {
    const inputEmail = useRef(null);
    const inputFirstName = useRef(null);
    const inputLastName = useRef(null);
    const [userData, setUserData] = useState(user);

    useEffect(() => {
        setUserData(user);
    }, [user]);

    const handleChange = (e, key) => {
        setUserData({
            ...userData,
            [key]: e.target.value
        });

        console.log(userData)
    }

    const handleSave = () => {
        try {
            makeApiRequest(`/users/${user.id}`, 'PUT', {
                email: inputEmail.current.value,
                first_name: inputFirstName.current.value,
                last_name: inputLastName.current.value
            }).then(resp => {
                console.log(resp.data);
                // setUsers(resp.data);
            });
        }
        catch(err) {
            // show error
        }
    }

    return (
        <div className='user__item'>
            <img src={user.avatar} className='user__avatar' alt='avatar'/>
            <div className='user__item-content'>
                {!isEdit
                    ? <Fragment>
                        <p>{user.email}</p>
                        <p>{user.first_name}</p>
                        <p>{user.last_name}</p>
                    </Fragment>
                    : <Fragment>
                        <input className='user__data' defaultValue={userData.email} ref={inputEmail}/>
                        <input className='user__data' defaultValue={userData.first_name} ref={inputFirstName}/>
                        <input className='user__data' defaultValue={userData.last_name} ref={inputLastName}/>
                    </Fragment>
                }
            </div>
            {isEdit && (
                <div>
                    <Button label='Save' color='green' onClick={handleSave} />
                </div>
            )}
        </div>
    )
}

export default User;
