import React, { Fragment, useState, useEffect } from 'react';
import UserDataItem from './UserDataItem';

function User({ user, isEdit = false }) {
    const [userData, setUserData] = useState(user);

    useEffect(() => {
        setUserData(user);
    }, [user]);

    return (
        <div className='user__item'>
            <img src={user.avatar} className='user__avatar' alt='avatar'/>
            <div className='user__item-content'>
                {isEdit
                    ? <Fragment>
                        <p>{user.email}</p>
                    </Fragment>
                    : <Fragment>
                        <input className='user__data' value={user.value} onChange={handleChange}/>
                    </Fragment>
}
                <UserDataItem key='email' isEdit={isEdit} user={userData} setUserData={setUserData}/>
                <UserDataItem key='first_name' isEdit={isEdit} user={userData} setUserData={setUserData}/>
                <UserDataItem key='last_name' isEdit={isEdit} user={userData} setUserData={setUserData}/>
            </div>
            {/* <button onClick={handleToggleEdit}>Edit</button> */}
        </div>
    )
}

export default User;
