import React, { Fragment, useEffect, useState } from 'react';
import User from './User';
import Button from './Button';
import makeApiRequest from '../makeApiReqest';

function UserList({ toggleEdit, currentUserId }) {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        if (!users.length) {
            try {
                makeApiRequest('/users?page=4').then(resp => {
                    console.log(resp.data);
                    setUsers(resp.data);
                });
            }
            catch(err) {
                // show error
            }
        }
    }, [users]);

    return (
        <div className='user__list'>
            {users.map((user, i) => (
                <div className={`user__list-item ${i % 2 === 0 ? 'highlight' : ''}`}>
                    <User user={user} isEdit={currentUserId === user.id}/>
                    <div className={`user__actions ${currentUserId === user.id ? 'active' : ''}`}>
                        {currentUserId !== user.id && (
                            <Fragment>
                                <Button label='Edit' color='blue' onClick={() => toggleEdit(user)} />
                                <Button label='Delete' color='red' />
                            </Fragment>
                        )}
                    </div>
                </div>
            ))}
        </div>
    );
}

export default UserList;
