import React, { useEffect, useState } from 'react';
import User from './User';
import makeApiRequest from '../makeApiReqest';

function UserList() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        if (!users.length) {
            try {
                async function getUsers() {
                    return await makeApiRequest('/users?page=2');
                }

                const { data } = getUsers();

                setUsers(data);
            }
            catch(err) {
                // show error
            }
        }
    }, [users]);

    return (
        <div>
            {users.map(user => (
                <User user={user} />
            ))}
        </div>
    );
}

export default UserList;
