import React, { Fragment, useState, useEffect } from 'react';
import UserDataItem from './UserDataItem';

function User({ user, isEdit = false }) {
    const [userData, setUserData] = useState(user);

    useEffect(() => {
        setUserData(user);
    }, [user]);

    const handleChange = (e, key) => {
        setUserData({
            ...user,
            [key]: e.target.value
        });
    }

    return (
        <div className='user__item'>
            <img src={user.avatar} className='user__avatar' alt='avatar'/>
            <div className='user__item-content'>
                {!isEdit
                    ? <Fragment>
                        <p>{user.email}</p>
                        <p>{user.first_name}</p>
                        <p>{user.last_name}</p>
                    </Fragment>
                    : <Fragment>
                        <input className='user__data' value={user.email} onChange={e => handleChange(e, 'email')}/>
                        <input className='user__data' value={user.first_name} onChange={e => handleChange(e, 'first_name')}/>
                        <input className='user__data' value={user.last_name} onChange={e => handleChange(e, 'last_name')}/>
                    </Fragment>
                }
            </div>
            {/* <button onClick={handleToggleEdit}>Edit</button> */}
        </div>
    )
}

export default User;
