import PropTypes from 'prop-types';
import React, { useState } from 'react';

function UserDataItem({ user, isEdit, key, setUserData }) {
    const handleChange = (e) => {
        setUserData({
            ...user,
            [key]: e.target.value
        });
    }

    return !isEdit ? <p>{user[key]}</p> : <input value={user[key]} onChange={handleChange}/>
}

export default UserDataItem;
