import React, { Fragment, useState, useEffect } from 'react';
import Button from './Button';
import makeApiRequest from '../makeApiReqest';

function User({ user, isEdit = false }) {
    const [userData, setUserData] = useState(user);

    useEffect(() => {
        setUserData(user);
    }, [user]);

    const handleChange = (e, key) => {
        setUserData({
            ...userData,
            [key]: e.target.value
        });

        console.log(userData)
    }

    const handleSave = () => {
        try {
            makeApiRequest(`/users/${user.id}`, 'PUT', userData).then(resp => {
                console.log(resp.data);
                // setUsers(resp.data);
            });
        }
        catch(err) {
            // show error
        }
    }

    return (
        <div className='user__item'>
            <img src={user.avatar} className='user__avatar' alt='avatar'/>
            <div className='user__item-content'>
                {!isEdit
                    ? <Fragment>
                        <p>{user.email}</p>
                        <p>{user.first_name}</p>
                        <p>{user.last_name}</p>
                    </Fragment>
                    : <Fragment>
                        <input className='user__data' defaultChecked={userData.email} id='email'/>
                        <input className='user__data' defaultChecked={userData.first_name} id='first_name'/>
                        <input className='user__data' defaultChecked={userData.last_name} id='last_name'/>
                    </Fragment>
                }
            </div>
            {isEdit && (
                <div>
                    <Button label='Save' color='green' onClick={handleSave} />
                </div>
            )}
        </div>
    )
}

export default User;
